import React, { Component } from 'react';
import PortfolioContainer from './PortfolioContainer';
import FormContainer from './FormContainer';
import NavBar from '../components/NavBar'
import tokens from '../config_keys.js'

export default class MainContainer extends Component {

    state = {
        userStocks: [],
        marketStocks: [],
        currentUser: {}
    }

    componentDidMount(){
        //get the current users stocks from backend
        fetch("http://localhost:3000/stocks")
        .then(r => r.json())
        .then((stocksResponse) => {
            //console.log(stocksResponse)
            this.setState({
                userStocks: stocksResponse
            })
        })

        // get the market stocks from api
    }

    getStockInfo=()=>{
        fetch(`https://www.alphavantage.co/query?function=GLOBAL_QUOTE&symbol=AMZN&apikey=${tokens.AlPHAV_TOKEN}`)
        .then(r => r.json())
        .then((theActualResponse) => {console.log(theActualResponse)})
    }
    render() {
        this.getStockInfo()
        return (
            <div>
            <NavBar/>
                <div className="row">
                    <div className="col-8">
                        <PortfolioContainer stocks = {this.state.userStocks}/>
                    </div>
                    <div className="col-4">
                        <FormContainer/>
                    </div>
                </div>
            </div>
        )
    }
}
