import React, { Component } from 'react';
import IEX_TOKEN from '../config_keys';

export default class FormContainer extends Component {
    state = {
        balance: 5000.00,
        ticker: '',
        quantity: 1
    }

    handleChange = (evt)=>{
        this.setState({
            [evt.target.name]: evt.target.value
        })
    }

    handleSubmit = (evt) =>{
        evt.preventDefault()
        fetch(`https://cloud.iexapis.com/stable/stock/${this.state.ticker}/quote?token=${IEX_TOKEN}`)
        .then(r => r.json())
        .then((theActualResponse) => {console.log(theActualResponse)})
    }
    render() {
        console.log(this.state)
        return (
            <div>
                <h2>Purchase Stocks</h2>

                <h5>Your Balance - ${this.state.balance}</h5>

                <form onSubmit = {this.handleSubmit}>
                    <label >Ticker:</label><br/>
                    <input onChange = {this.handleChange} type="text" id="ticker" name="ticker" placeholder="AMZN" value = {this.state.ticker}></input><br/><br/>
                    <label >Quantity:</label><br/>
                    <input onChange = {this.handleChange} type="number" id ="quantity" name="quantity" placeholder= "1" value = {this.state.quantity}></input><br/><br/>
                    <input type="submit" value="Get Price"></input>
                </form>

            </div>
        )
    }
}
