import React, { Component } from 'react';
import tokens from '../config_keys';

export default class FormContainer extends Component {
    state = {
        balance: 5000.00,
        ticker: '',
        quantity: 1,
        total: 0,
        transaction:{ticker: 'N/A', price: 0}
    }

    handleChange = (evt)=>{
        this.setState({
            [evt.target.name]: evt.target.value
        })
    }

    handleSubmit = (evt) =>{
        evt.preventDefault()
        // this.props.getStockInfo(this.state.ticker)
        fetch(`https://cloud.iexapis.com/stable/stock/${this.state.ticker}/quote?token=${tokens.IEX_TOKEN}`)
        .then(r => r.json())
        .then((stockObj) => {
            console.log(stockObj)
            const newTotal = this.state.quantity * stockObj.latestPrice
            const transaction = { ticker: stockObj.symbol, price: stockObj.latestPrice}
            this.setState({
                transaction: transaction,
                total: newTotal
            })
        }).catch((error) => {
            alert("Please Verify Stock Symbol and Try Again")
        });
    }

    handleClick =(evt)=>{
        // console.log("buy")

    }
    render() {
        // console.log(this.state)
        const {price, ticker} = this.state.transaction
        return (
            <div className="pa4-l">
                <h2>Purchase Stocks</h2>

                <h5>Your Balance - ${this.state.balance}</h5>

                <form className="mw7 center pa4 br2-ns ba b--black-10" onSubmit = {this.handleSubmit}>
                <fieldset className="cf bn ma0 pa0">
                    <label >Ticker:</label><br/>
                    <input onChange = {this.handleChange} type="text" id="ticker" name="ticker" placeholder="AMZN" value = {this.state.ticker}></input><br/><br/>
                    <label >Quantity:</label><br/>
                    <input onChange = {this.handleChange} type="number" id ="quantity" name="quantity" placeholder= "1" value = {this.state.quantity}></input><br/><br/>
                    <input type="submit" value="Get Price"></input>
                    </fieldset>
                </form><br/><br/>
                <span><p>Cost Breakdown: {this.state.quantity} shares of {ticker} @ ${price}/share =  ${this.state.total} </p></span> <br/>
                <div>
                    <h4>Complete Transaction:</h4> {
                        this.state.total === 0 || this.state.total > this.state.balance? <span>N/A</span>: <button onClick ={this.handleClick}>Buy</button>
                    }
                </div>


            </div>
        )
    }
}
