import React, { Component } from 'react';
import {Link} from 'react-router-dom';

export default class LoginPage extends Component {
    state ={
        email: '',
        password: ''
    }

    handleChange = (evt) => {
        this.setState({[evt.target.name]: evt.target.value})
    }

    handleSubmit = (evt) => {
        evt.preventDefault()
        fetch('http://localhost:3000/login', {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(this.state)
        })
        .then(res => res.json())
        .then(data => {
            if (data.token) {
                localStorage.token = data.token

                this.props.history.push('/profile')
                // this.props.redirect('profile')

            }
        })
    }

    render() {
        console.log(this.state)
        return (
            <main className = "pa4 black-80">
                <form className = "measure-center" onSubmit={this.handleSubmit}>
                <fieldset id="sign_up" class="ba b--transparent ph0 mh0">
                <h1>Log in please!</h1>
                    <input onChange={this.handleChange} value={this.state.email} type="text" name="email" placeholder= "email"/>
                    <input onChange={this.handleChange} value={this.state.password} type="password" name="password" placeholder="password"/>
                    <input type="submit" value="Log in"/>

                    <Link to= '/signup' >
                        <h4 className="f6 link  db" >Signup</h4>
                    </Link>
                    </fieldset>
                </form>
            </main>
        )
    }
}
