import React, { Component } from 'react';
import PortfolioContainer from './PortfolioContainer';

export default class MainContainer extends Component {

    state = {
        stocks: []
    }

    componentDidMount(){
        fetch("http://localhost:3000/stocks")
        .then(r => r.json())
        .then((stocksResponse) => {
            //console.log(stocks)
            this.setState({
                stocks: stocksResponse
            })
        })
    }
    render() {
        return (
            <div>
                <PortfolioContainer/>
            </div>
        )
    }
}
