import React, { Component } from 'react';
import PortfolioContainer from './PortfolioContainer';
import FormContainer from './FormContainer';
import NavBar from '../components/NavBar'
import tokens from '../config_keys.js'

export default class MainContainer extends Component {

    state = {
        userStocks: [],
        marketStocks: [],
        currentUser: {}
    }

    componentDidMount(){
        //get the current users stocks from backend
        fetch("http://localhost:3000/holdings")
        .then(r => r.json())
        .then((stocksResponse) => {
            //console.log(stocksResponse)
            this.setState({
                userStocks: stocksResponse
            })
        })

        // get the market stocks from api
    }

    getStockInfo=(ticker)=>{
        // fetch(`https://cloud.iexapis.com/stable/stock/${ticker}/quote?token=${tokens.IEX_TOKEN}`)
        fetch(`https://www.alphavantage.co/query?function=GLOBAL_QUOTE&symbol=${ticker}&apikey=${tokens.AlPHAV_TOKEN}`)
        .then(r => r.json())
        .then((stockObj) => {
            console.log(stockObj["Global Quote"])
            const stock = { ticker: stockObj["Global Quote"]["01. symbol"], open: stockObj["Global Quote"]["02. open"], price: stockObj["Global Quote"]["05. price"], previousClose: stockObj["Global Quote"]["08. previous close"] }

            console.log(stock)
        })
    }
    render() {

        return (
            <div>
            <NavBar/>
                <div className="row">
                    <div className="col-8">
                        <PortfolioContainer stocks = {this.state.userStocks}/>
                    </div>
                    <div className="col-4">
                        <FormContainer getStockInfo = {this.getStockInfo}/>
                    </div>
                </div>
            </div>
        )
    }
}
