import React, { Component } from 'react';
import StockItem from '../components/StockItem'

export default class PortfolioContainer extends Component {
    state ={
        portfolioCurrentTotal: 0
    }


    componentDidMount(){

        const totals = this.props.stocks.map(stockObj=> (stockObj.price*stockObj.quantity))

        let sum = totals.reduce(function(a, b){
            return a + b;
        }, 0);

        this.setState({portfolioCurrentTotal: sum})

    }
    render() {
        return (
            <div>
                <h2 ><strong className ="text-capitalize">{this.props.currentUser.name}</strong>'s Portfolio(${this.state.portfolioCurrentTotal}) </h2>
                {
                    this.props.stocks.map(stockObj=> <StockItem 
                        stock={stockObj} 
                        key = {stockObj.id} getStockInfo = {this.props.getStockInfo}
                        />)
                }

            </div>
        )
    }
}
